PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS "migrations" ("id" integer primary key autoincrement not null, "migration" varchar not null, "batch" integer not null);
CREATE TABLE IF NOT EXISTS "users" ("id" integer primary key autoincrement not null, "name" varchar not null, "email" varchar not null, "email_verified_at" datetime, "password" varchar not null, "remember_token" varchar, "current_team_id" integer, "profile_photo_path" varchar, "created_at" datetime, "updated_at" datetime, "two_factor_secret" text, "two_factor_recovery_codes" text, "two_factor_confirmed_at" datetime, first_name VARCHAR(255) NOT NULL, last_name VARCHAR(255) NOT NULL);
CREATE TABLE IF NOT EXISTS "password_reset_tokens" ("email" varchar not null, "token" varchar not null, "created_at" datetime, primary key ("email"));
CREATE TABLE IF NOT EXISTS "sessions" ("id" varchar not null, "user_id" integer, "ip_address" varchar, "user_agent" text, "payload" text not null, "last_activity" integer not null, primary key ("id"));
CREATE TABLE IF NOT EXISTS "cache" ("key" varchar not null, "value" text not null, "expiration" integer not null, primary key ("key"));
CREATE TABLE IF NOT EXISTS "cache_locks" ("key" varchar not null, "owner" varchar not null, "expiration" integer not null, primary key ("key"));
CREATE TABLE IF NOT EXISTS "jobs" ("id" integer primary key autoincrement not null, "queue" varchar not null, "payload" text not null, "attempts" integer not null, "reserved_at" integer, "available_at" integer not null, "created_at" integer not null);
CREATE TABLE IF NOT EXISTS "job_batches" ("id" varchar not null, "name" varchar not null, "total_jobs" integer not null, "pending_jobs" integer not null, "failed_jobs" integer not null, "failed_job_ids" text not null, "options" text, "cancelled_at" integer, "created_at" integer not null, "finished_at" integer, primary key ("id"));
CREATE TABLE IF NOT EXISTS "failed_jobs" ("id" integer primary key autoincrement not null, "uuid" varchar not null, "connection" text not null, "queue" text not null, "payload" text not null, "exception" text not null, "failed_at" datetime not null default CURRENT_TIMESTAMP);
CREATE TABLE IF NOT EXISTS "twill_users" ("id" integer primary key autoincrement not null, "deleted_at" datetime, "created_at" datetime, "updated_at" datetime, "published" tinyint(1) not null default '0', "name" varchar not null, "email" varchar not null, "password" varchar, "role" varchar not null, "title" varchar, "description" text, "remember_token" varchar, "language" varchar, "last_login_at" datetime, "registered_at" datetime, "require_new_password" tinyint(1) not null default '0');
CREATE TABLE IF NOT EXISTS "twill_blocks" ("id" integer primary key autoincrement not null, "blockable_id" integer, "blockable_type" varchar, "position" integer not null, "content" text not null, "type" varchar not null, "child_key" varchar, "parent_id" integer, "editor_name" varchar);
CREATE TABLE IF NOT EXISTS "twill_medias" ("id" integer primary key autoincrement not null, "created_at" datetime, "updated_at" datetime, "deleted_at" datetime, "uuid" text not null, "alt_text" text, "width" integer not null, "height" integer not null, "caption" text, "filename" text);
CREATE TABLE IF NOT EXISTS "twill_mediables" ("id" integer primary key autoincrement not null, "created_at" datetime, "updated_at" datetime, "deleted_at" datetime, "mediable_id" integer, "mediable_type" varchar, "media_id" integer not null, "crop_x" integer, "crop_y" integer, "crop_w" integer, "crop_h" integer, "role" varchar, "crop" varchar, "lqip_data" text, "ratio" varchar, "metadatas" text not null, "locale" varchar not null default 'en', "position" integer not null default '1', foreign key("media_id") references "twill_medias"("id") on delete cascade on update cascade);
CREATE TABLE IF NOT EXISTS "twill_files" ("id" integer primary key autoincrement not null, "created_at" datetime, "updated_at" datetime, "deleted_at" datetime, "uuid" text not null, "filename" text, "size" integer not null);
CREATE TABLE IF NOT EXISTS "twill_settings" ("id" integer primary key autoincrement not null, "created_at" datetime, "updated_at" datetime, "deleted_at" datetime, "key" varchar, "section" varchar);
CREATE TABLE IF NOT EXISTS "twill_setting_translations" ("id" integer primary key autoincrement not null, "twill_setting_id" integer not null, "deleted_at" datetime, "created_at" datetime, "updated_at" datetime, "locale" varchar not null, "active" tinyint(1) not null default '0', "value" text, foreign key("twill_setting_id") references "twill_settings"("id") on delete CASCADE);
CREATE TABLE IF NOT EXISTS "twill_tagged" ("id" integer primary key autoincrement not null, "taggable_type" varchar not null, "taggable_id" integer not null, "tag_id" integer not null);
CREATE TABLE IF NOT EXISTS "twill_tags" ("id" integer primary key autoincrement not null, "namespace" varchar not null, "slug" varchar not null, "name" varchar not null, "count" integer not null default '0');
CREATE TABLE IF NOT EXISTS "twill_features" ("id" integer primary key autoincrement not null, "featured_id" varchar not null, "featured_type" varchar not null, "bucket_key" varchar not null, "position" integer not null, "starred" tinyint(1) not null default '0', "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "twill_related" ("id" integer primary key autoincrement not null, "subject_id" integer, "subject_type" varchar not null, "related_id" integer, "related_type" varchar not null, "browser_name" varchar not null, "position" integer not null);
CREATE TABLE IF NOT EXISTS "twill_fileables" ("id" integer primary key autoincrement not null, "created_at" datetime, "updated_at" datetime, "deleted_at" datetime, "file_id" integer not null, "fileable_id" integer, "fileable_type" varchar, "role" varchar, "locale" varchar not null, "position" integer not null default '1', foreign key("file_id") references twill_files("id") on delete cascade on update cascade);
CREATE TABLE IF NOT EXISTS "activity_log" ("id" integer primary key autoincrement not null, "log_name" varchar, "description" text not null, "subject_id" integer, "subject_type" varchar, "causer_id" integer, "causer_type" varchar, "properties" text, "created_at" datetime, "updated_at" datetime, "event" varchar, "batch_uuid" varchar);
CREATE TABLE IF NOT EXISTS "app_settings" ("id" integer primary key autoincrement not null, "deleted_at" datetime, "created_at" datetime, "updated_at" datetime, "published" tinyint(1) not null default '0', "name" varchar);
CREATE TABLE IF NOT EXISTS "twill_password_resets" ("email" varchar not null, "token" varchar not null, "created_at" datetime, foreign key("email") references "twill_users"("email") on delete cascade on update cascade);
CREATE TABLE IF NOT EXISTS "personal_access_tokens" ("id" integer primary key autoincrement not null, "tokenable_type" varchar not null, "tokenable_id" integer not null, "name" varchar not null, "token" varchar not null, "abilities" text, "last_used_at" datetime, "expires_at" datetime, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "homes" ("id" integer primary key autoincrement not null, "deleted_at" datetime, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "home_translations" ("id" integer primary key autoincrement not null, "home_id" integer not null, "deleted_at" datetime, "created_at" datetime, "updated_at" datetime, "locale" varchar not null, "active" tinyint(1) not null default '0', "title" varchar, "meta_title" varchar, "meta_description" varchar, foreign key("home_id") references "homes"("id") on delete CASCADE);
CREATE TABLE IF NOT EXISTS "home_revisions" ("id" integer primary key autoincrement not null, "home_id" integer not null, "user_id" integer, "created_at" datetime, "updated_at" datetime, "payload" text not null, foreign key("home_id") references "homes"("id") on delete cascade, foreign key("user_id") references "twill_users"("id") on delete set null);
CREATE TABLE IF NOT EXISTS "navigations" ("id" integer primary key autoincrement not null, "deleted_at" datetime, "created_at" datetime, "updated_at" datetime, "published" tinyint(1) not null default '0', "_lft" integer not null default '0', "_rgt" integer not null default '0', "parent_id" integer, "position" integer, "url" varchar, "has_submenu" tinyint(1) not null default '0', "submenu" text);
CREATE TABLE IF NOT EXISTS "navigation_translations" ("id" integer primary key autoincrement not null, "navigation_id" integer not null, "deleted_at" datetime, "created_at" datetime, "updated_at" datetime, "locale" varchar not null, "active" tinyint(1) not null default '0', "title" varchar, "description" text, foreign key("navigation_id") references "navigations"("id") on delete CASCADE);
CREATE TABLE IF NOT EXISTS "pages" ("id" integer primary key autoincrement not null, "deleted_at" datetime, "created_at" datetime, "updated_at" datetime, "published" tinyint(1) not null default '0', "_lft" integer not null default '0', "_rgt" integer not null default '0', "parent_id" integer, "position" integer);
CREATE TABLE IF NOT EXISTS "page_translations" ("id" integer primary key autoincrement not null, "page_id" integer not null, "deleted_at" datetime, "created_at" datetime, "updated_at" datetime, "locale" varchar not null, "active" tinyint(1) not null default '0', "title" varchar, "meta_title" varchar, "meta_description" varchar, foreign key("page_id") references "pages"("id") on delete CASCADE);
CREATE TABLE IF NOT EXISTS "page_slugs" ("id" integer primary key autoincrement not null, "page_id" integer not null, "deleted_at" datetime, "created_at" datetime, "updated_at" datetime, "slug" varchar not null, "locale" varchar not null, "active" tinyint(1) not null, foreign key("page_id") references "pages"("id") on delete CASCADE on update NO ACTION);
CREATE TABLE IF NOT EXISTS "page_revisions" ("id" integer primary key autoincrement not null, "page_id" integer not null, "user_id" integer, "created_at" datetime, "updated_at" datetime, "payload" text not null, foreign key("page_id") references "pages"("id") on delete cascade, foreign key("user_id") references "twill_users"("id") on delete set null);
CREATE TABLE IF NOT EXISTS "profile_translations" ("id" integer primary key autoincrement not null, "profile_id" integer not null, "deleted_at" datetime, "created_at" datetime, "updated_at" datetime, "locale" varchar not null, "active" tinyint(1) not null default '0', foreign key("profile_id") references "profiles"("id") on delete CASCADE);
CREATE TABLE IF NOT EXISTS "profile_slugs" ("id" integer primary key autoincrement not null, "profile_id" integer not null, "deleted_at" datetime, "created_at" datetime, "updated_at" datetime, "slug" varchar not null, "locale" varchar not null, "active" tinyint(1) not null, foreign key("profile_id") references "profiles"("id") on delete CASCADE on update NO ACTION);
CREATE TABLE IF NOT EXISTS "roles" ("id" integer primary key autoincrement not null, "name" varchar not null, "description" varchar, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "programs" ("id" integer primary key autoincrement not null, "deleted_at" datetime, "created_at" datetime, "updated_at" datetime, "published" tinyint(1) not null default '0', "position" integer, "_lft" integer not null default '0', "_rgt" integer not null default '0', "parent_id" integer);
CREATE TABLE IF NOT EXISTS "program_translations" ("id" integer primary key autoincrement not null, "program_id" integer not null, "deleted_at" datetime, "created_at" datetime, "updated_at" datetime, "locale" varchar not null, "active" tinyint(1) not null default '0', "title" varchar, "tagline" text, current_belt VARCHAR(255) DEFAULT NULL, foreign key("program_id") references "programs"("id") on delete CASCADE);
CREATE TABLE IF NOT EXISTS "program_slugs" ("id" integer primary key autoincrement not null, "program_id" integer not null, "deleted_at" datetime, "created_at" datetime, "updated_at" datetime, "slug" varchar not null, "locale" varchar not null, "active" tinyint(1) not null, foreign key("program_id") references "programs"("id") on delete CASCADE on update NO ACTION);
CREATE TABLE IF NOT EXISTS "program_revisions" ("id" integer primary key autoincrement not null, "program_id" integer not null, "user_id" integer, "created_at" datetime, "updated_at" datetime, "payload" text not null, foreign key("program_id") references "programs"("id") on delete cascade, foreign key("user_id") references "twill_users"("id") on delete set null);
CREATE TABLE IF NOT EXISTS "profile_program" ("profile_id" integer not null, "program_id" integer not null, "position" integer not null, "belt_id" integer, created_at TIMESTAMP NOT NULL DEFAULT TIMESTAMP, updated_at TIMESTAMP NOT NULL DEFAULT TIMESTAMP, deleted_at TIMESTAMP NULL DEFAULT NULL, "program_belt_id" integer, foreign key("program_id") references programs("id") on delete cascade on update no action, foreign key("profile_id") references profiles("id") on delete cascade on update no action, foreign key("belt_id") references "belts"("id") on delete set null);
CREATE TABLE IF NOT EXISTS "profile_program_belt" ("id" integer primary key autoincrement not null, "profile_id" integer not null, "program_belt_id" integer not null, "created_at" datetime, "updated_at" datetime, foreign key("profile_id") references "profiles"("id") on delete cascade, foreign key("program_belt_id") references "program_belts"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "profiles" ("id" integer primary key autoincrement not null, "deleted_at" datetime, "created_at" datetime, "updated_at" datetime, "published" tinyint(1) not null default ('0'), "first_name" varchar, "last_name" varchar, "bio" text, "email" varchar, "program_user_id" integer, "address" varchar, "city" varchar, "state" varchar, "zip" varchar, "user_id" integer not null, "name" varchar(255) not null, "program_belt_id" int(20) default (NULL), "current_belt" integer, foreign key("user_id") references users("id") on delete cascade on update cascade, foreign key("current_belt") references "program_belts"("id"));
CREATE TABLE IF NOT EXISTS "program_belts" ("id" integer primary key autoincrement not null, "deleted_at" datetime, "created_at" datetime, "updated_at" datetime, "published" tinyint(1) not null default '0', "name" varchar, "category" varchar, "color" varchar, "program_id" integer not null, "position" integer, "profile_id" integer, "requirements" text);
CREATE TABLE IF NOT EXISTS "program_belt_slugs" ("id" integer primary key autoincrement not null, "program_belt_id" integer not null, "deleted_at" datetime, "created_at" datetime, "updated_at" datetime, "slug" varchar not null, "locale" varchar not null, "active" tinyint(1) not null, foreign key("program_belt_id") references "program_belts"("id") on delete CASCADE on update NO ACTION);
CREATE TABLE IF NOT EXISTS "belts" ("id" integer primary key autoincrement not null, "deleted_at" datetime, "created_at" datetime, "updated_at" datetime, "published" tinyint(1) not null default '0', "program_id" integer not null);
CREATE TABLE IF NOT EXISTS "belt_translations" ("id" integer primary key autoincrement not null, "belt_id" integer not null, "deleted_at" datetime, "created_at" datetime, "updated_at" datetime, "locale" varchar not null, "active" tinyint(1) not null default '0', "name" varchar, "category" varchar, "color" varchar, foreign key("belt_id") references "belts"("id") on delete CASCADE);
